import React, { Component } from 'react';
import './App.css';

class App extends Component {
  state = {
    posts: []
  };


  async componentDidMount() {
    try {
      const res = await fetch('/api/posts/'); // fetching the data from api, before the page loaded
      const todos = await res.json();
      this.setState({
        posts
      });
    } catch (e) {
      console.log(e);
    }
  }

  render() {
    return (
      <div>
        {this.state.posts.map(item => (
          <div key={item.id}>
            <h1>{item.title}</h1>
            <span>{item.content}</span>
            <span>{item.created_at}</span>
            <span>{item.updated_at}</span>
          </div>
        ))}
      </div>
    );
  }
}
export default App;
